import { makeAutoObservable } from "mobx";
import { message } from "antd";

import {
    login,
    register,
    getTomatos,
    addReport,
    getGoodList,
    buyGood,
    adminSelfRoom,
    placeSelfRoom,
    joinRoom
} from "../services/index.js";

class Mobx {
    tomato = 0
    tomatoList = []
    goodList = []
    room = {}
    member = []

    constructor() {
        makeAutoObservable(this);
    }

    loginRequest = async(data) => {
        const {
            data: {
                code, errors,
                data: {
                    token, tomato,
                    vip, admin, email
                }
            }
        } = await login(data);

        if (code) {
            this.tomato = tomato;
            localStorage.setItem("token", token);
            localStorage.setItem("vip", vip);
            localStorage.setItem("admin", admin);
            localStorage.setItem("username", data.username);
            localStorage.setItem("email", email);
            message.success("登录成功");
            return true;
        } else {
            message.error(errors);
            return false;
        }
    }

    registerRequest = async(data) => {
        const {
            data: {
                code, errors,
                data: {
                    token, tomato,
                    vip, admin, email
                }
            }
        } = await register(data);

        if (code) {
            this.tomato = tomato;
            localStorage.setItem("token", token);
            localStorage.setItem("vip", vip);
            localStorage.setItem("admin", admin);
            localStorage.setItem("username", data.username);
            localStorage.setItem("email", email);
            message.success("注册成功自动登录");
            return true;
        } else {
            message.error(errors);
            return false;
        }
    }

    getTomatosRequest = async() => {
        const {
            data: {
                code, errors,
                data
            }
        } = await getTomatos();

        const { user_report } = data[0];

        if (code) {
            this.tomatoList = user_report;
        } else {
            message.error(errors);
        }
    }

    addReportRequest = async(data) => {
        const {
            data: {
                code, errors
            }
        } = await addReport(data);

        if (code) {
            message.success("任务记录已上传");
        } else {
            message.error(errors);       
        }
    }

    getGoodListRequest = async() => {
        const {
            data: {
                code, errors,
                data
            }
        } = await getGoodList();

        if (code) {
            this.goodList = data;
        } else {
            message.error(errors);
        }
    }

    buyGoodRequest = async(data) => {
        const {
            data: {
                code, errors
            }
        } = await buyGood(data);

        if (code) {
            message.success("购买成功");
            this.goodList = this.goodList.map(item => {
                if (item.id === data.goods) {
                    return {
                        ...item,
                        number: item.number - 1
                    }
                } else {
                    return item;
                }
            })
        } else {
            message.error(errors);
        }
    }

    adminSelfRoomRequest = async() => {
        const {
            data: {
                code, data, errors
            }
        } = await adminSelfRoom();
        const { member } = data[0];

        if (code) {
            this.room = data[0];
            this.member = member;
        } else {
            message.error(errors);
        }
    }

    placeSelfRoomRequest = async() => {
        const event = await placeSelfRoom();
        console.log(event);
    }

    joinRoomRequest = async(data) => {
        const event = await joinRoom(data);
        console.log(event);
    }
}

const myStore = new Mobx();
export default myStore;
