import React, { useEffect, useState } from "react";
import {
    Empty, Modal, Upload,
    InputNumber, Input, Button
} from "antd";
import { observer } from "mobx-react-lite";
import { useNavigate } from "react-router-dom";
import myStore from "../../store";
import "./index.css";
import {
    LeftCircleOutlined, UploadOutlined
} from '@ant-design/icons';

function Manage() {
    const navigate = useNavigate();
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        myStore.getGoodListRequest()
    }, [])

    const handleModify = (id) => {
        console.log(id)
        setIsVisible(true);
    }

    const handleReturn = () => {
        navigate("/home");
    }

    const handleAdd = () => {
        console.log("点击添加");
        setIsVisible(true);
    }

    const handleOk = () => {
        setIsVisible(false);
    }

    const handleCancel = () => {
        setIsVisible(false);
    }

    return (
        <div className="on-content">
            <Modal
                title="番茄学习法"
                visible={isVisible}
                onOk={handleOk}
                onCancel={handleCancel}
            >
                <div className="man-input">
                    <div className="man-label">名字</div>
                    <Input className="man-input-inner" />
                </div>
                <div className="man-input">
                    <div className="man-label">花费</div>
                    <InputNumber className="man-inputnumber-inner" min={1} />
                </div>
                <div className="man-input">
                    <div className="man-label">数量</div>
                    <InputNumber className="man-inputnumber-inner" min={1} />
                </div>
                <Upload
                    className="man-upload"
                >
                    <Button icon={<UploadOutlined />}>Click to Upload</Button>
                </Upload>
            </Modal>
            <LeftCircleOutlined
                className="on-return"
                onClick={handleReturn}
            />
            <div className="on-header">
                番茄果园
            </div>
            <div className="on-good">
                {
                    myStore.goodList.length === 0
                        ? <Empty
                            className="on-empty"
                            description="没有数据"
                            image={Empty.PRESENTED_IMAGE_SIMPLE}
                        />
                        : myStore.goodList.map(({ id, name, price, number, img }) => {
                            return (
                                <div className="on-item" key={id}>
                                    <div className="on-left">
                                        <div className="on-item-img"></div>
                                    </div>
                                    <div className="on-right">
                                        <div className="on-item-name">{name}</div>
                                        <div className="on-item-number">存量: {number}</div>
                                        <div className="on-item-price">花费: {price}</div>
                                        <div
                                            className="on-btn"
                                            onClick={() => handleModify(id)}
                                        >修改</div>
                                    </div>
                                </div>
                            )
                        })
                }
                <div
                    className="man-add"
                    onClick={handleAdd}
                >
                    <div className="add-column"></div>
                    <div className="add-row"></div>
                </div>
            </div>
        </div>
    )
}

export default observer(Manage);
