import { useState } from "react";
import { Modal, Input, InputNumber } from "antd";
import { PlusCircleOutlined } from '@ant-design/icons';
import "./index.css";

const dataList = [
    {
        tip: "学习",
        now: "2022-03-07T02:08:44.025787",
        comment: "这学习多是一件美逝",
        expected_time: "60",
        actual_time: "60"
    },
    {
        tip: "学习",
        now: "2022-03-07T02:08:44.025787",
        comment: "这学习多是一件美逝",
        expected_time: "60",
        actual_time: "60"
    },
    {
        tip: "学习",
        now: "2022-03-07T02:08:44.025787",
