        now: "2022-03-07T02:08:44.025787",
        comment: "这学习多是一件美逝",
        expected_time: "60",
        actual_time: "60"
    },
    {
        tip: "学习",
        now: "2022-03-07T02:08:44.025787",
        comment: "这学习多是一件美逝",
        expected_time: "60",
        actual_time: "60"
    },
    {
        tip: "学习",
        now: "2022-03-07T02:08:44.025787",
        comment: "这学习多是一件美逝",
        expected_time: "60",
        actual_time: "60"
    },
]

function Card() {
    const [isVisible, setVisible] = useState(false);

    const handleCreate = () => {
        setVisible(true);
    }

    return (
        <div className="ca-ma">
            <div className="card">
                <div className="ca-header">番茄列表</div>
                <PlusCircleOutlined
                    className="ca-plus"
                    onClick={handleCreate}
                />
                <Modal
                    title="番茄学习法"
                    visible={isVisible}
                >
                    <div className="ca-modal">
                        <div className="ca-td">
                            <div className="ca-label">标签：</div>
                            <Input className="ca-input" placeholder="输入标签" />
                        </div>
                        <div className="ca-td">
                            <div className="ca-label">激励：</div>
                            <Input className="ca-input" placeholder="来句鼓励自己" />
                        </div>
                        <div className="ca-td">
                            <div className="ca-label">时间：</div>
                            <InputNumber />
                        </div>
                    </div>
                </Modal>
                <div className="ca-list">
                    {
                        dataList.map((item, index) => {
                            const {
                                tip, now, comment,
                                expected_time, actual_time
                            } = item;

                            return <div className="ca-item">
                                <div className="ca-top">
                                    <div className="ca-tip">{tip}</div>
                                    <div className="ca-start-time">{now}</div>
                                </div>
                                <div className="ca-comment">{comment}</div>
                                <div className="ca-bto">
                                    <div className="ca-left-time">预计：{expected_time} 分钟</div>
                                    <div className="ca-right-time">实际：{actual_time} 分钟</div>
                                </div>
                            </div>
                        })
                    }
                </div>
            </div>
        </div>
    )
}

export default Card;
