import { useEffect } from "react";
import { Empty, Modal } from "antd";
import myStore from "../../store";
import "./index.css";

const { confirm } = Modal;

function Onchard() {
    useEffect(async() => {
        await myStore.getGoodListRequest();
        console.log(myStore.goodList);
    }, [])

    const handleBuy = (id) => {
        confirm({
            title: "番茄果园",
            content: "是否确认购买",
            okText: "确认",
            cancelText: "取消",
            onOk() {
                myStore.buyGoodRequest({
                    goods: id
                });
            }
        })
    }

    return (
        <div className="on-content">
            <div className="on-header">
                Forest 是一个帮助您放下手机，专注生活的 App。
            </div>
            <div className="on-good">
                {
                    myStore.goodList.length === 0
                        ? <Empty
                            className="on-empty"
                            description="没有数据"
                            image={Empty.PRESENTED_IMAGE_SIMPLE}
                        />
                        : myStore.goodList.map(({id, name, price, number, img}) => {
                            return (
                                <div className="on-item" key={id}>
                                    <div className="on-left">
                                        <div className="on-item-img"></div>
                                    </div>
                                    <div className="on-right">
                                        <div className="on-item-name">{name}</div>
                                        <div className="on-item-number">存量: {number}</div>
                                        <div className="on-item-price">花费: {price}</div>
                                        <div
                                            className="on-btn"
                                            onClick={() => handleBuy(id)}
                                        >购买</div>
                                    </div>
                                </div>
                            )
                        })
                }
            </div>
        </div>
    )
}

export default Onchard;
