import { useEffect, useState } from "react";
import { Empty, Modal, message } from "antd";
import myStore from "../../store";
import "./index.css";
import { getGoodList } from "../../services";
const { confirm } = Modal;
import DevTools from 'mobx-react-devtools'
function Onchard() {
    const [goodList, setGoodList] = useState([]);
    useEffect(() => {
        // myStore.getGoodListRequest();
        (async function () {
            const {
                data: {
                    code, errors,
                    data
                }
            } = await getGoodList();

            if (code) {
                console.log(data);
                // this.goodList = data;
                setGoodList(data);
            } else {
                message.error(errors);
            }
        })()
    }, [])

    const handleBuy = (id) => {
        confirm({
            title: "番茄果园",
            content: "是否确认购买",
            okText: "确认",
            cancelText: "取消",
            onOk() {
                myStore.buyGoodRequest({
                    goods: id
                });
                myStore.modifyNumber(id);
            }
        })
    }

    return (
        <div className="on-content">
      <DevTools />

            <div className="on-header">
                Forest 是一个帮助您放下手机，专注生活的 App。
            </div>
            <div className="on-good">
                {goodList.length}
                {
                    goodList.length === 0
                        ? <Empty
                            className="on-empty"
                            description="没有数据"
                            image={Empty.PRESENTED_IMAGE_SIMPLE}
                        />
                        : goodList.map(({ id, name, price, number, img }) => {
                            return (
                                <div className="on-item" key={id}>
                                    <div className="on-left">
                                        <div className="on-item-img"></div>
                                    </div>
                                    <div className="on-right">
                                        <div className="on-item-name">{name}</div>
                                        <div className="on-item-number">存量: {number}</div>
                                        <div className="on-item-price">花费: {price}</div>
                                        <div
                                            className="on-btn"
                                            onClick={() => handleBuy(id)}
                                        >购买</div>
                                    </div>
                                </div>
                            )
                        })
                }
            </div>
        </div>
    )
}

export default Onchard;
